    public void refresh() {
        inlibre();
        notlibre();
    }
    
    public void inlibre() {
        try {
            Connection connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/number_1", "root","");
            String bquery = "Select * from book where exist_in_database = 1 order by b_name";
            Statement sta = connection.createStatement();
            ResultSet bs = sta.executeQuery(bquery);
            DefaultListModel book = new DefaultListModel();
            while (bs.next()) {
                book.addElement(bs.getString("b_id") + " - " + bs.getString("b_name"));
            }
            belist.setModel(book);
            connection.close();
        } catch (Exception exception) {
            exception.printStackTrace();
        }
    }
    
    public void notlibre() {
        try {
            Connection connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/number_1", "root","");
            String bquery = "Select * from book where exist_in_database = 0 order by b_name";
            Statement sta = connection.createStatement();
            ResultSet bs = sta.executeQuery(bquery);
            DefaultListModel book = new DefaultListModel();
            while (bs.next()) {
                book.addElement(bs.getString("b_id") + " - " + bs.getString("b_name"));
            }
            bnlist.setModel(book);
            connection.close();
        } catch (Exception exception) {
            exception.printStackTrace();
        }
    }
    
