    private void bnlistValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_bnlistValueChanged
        int index = bnlist.getSelectedIndex();
        if(index >= 0) {
            String s = (String) bnlist.getSelectedValue();
            String[] str = s.split(" - ");
            String id = str[0].trim();
            String bn = str[1].trim();
            getvinfo(id, bn);
            System.out.println(index);
        }
    }//GEN-LAST:event_bnlistValueChanged
    
    void getvinfo(String id, String name) {
        try {
            Connection connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/number_1", "root", "");
            String query = "Select * from book where b_id = \"" + id + "\" and b_name = \""+name+"\"";
            Statement sta = connection.createStatement();
            ResultSet rs = sta.executeQuery(query);
            while (rs.next()) {
                getinfo(rs.getString("v_id"));
            }
            System.out.println("Get infor for " + id );
            connection.close();
        } catch (Exception exception) { //catch any exceptions that may have occured
            exception.printStackTrace(); //print any errors
        }
    }
    void getinfo(String id) {
        try {
            Connection connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/number_1", "root", "");
            String query = "Select * from vendor where v_id = " + id;
            Statement sta = connection.createStatement();
            ResultSet rs = sta.executeQuery(query);
            while (rs.next()) {
                System.out.println("Herer");
                vid.setText(rs.getString("v_id"));
                vnt.setText(rs.getString("v_name"));
                vat.setText(rs.getString("v_address"));
                vpt.setText(rs.getString("v_phone"));
            }
            System.out.println("Get infor for " + id );
            connection.close();
        } catch (Exception exception) { //catch any exceptions that may have occured
            exception.printStackTrace(); //print any errors
        }
    }
    
    DefaultListModel bre = new DefaultListModel();
    private void addbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addbActionPerformed
        String s = (String) bnlist.getSelectedValue();
        System.out.println(s);
        int i = bre.getSize();
        while (i > 0) {
            if (s.equals(bre.getElementAt(i-1).toString())) {
                bre.removeElementAt(i-1);
                System.out.println("Not Removing");
                JOptionPane.showMessageDialog(this, "This Book already in the List!");
            }
            i--;
        }
        bre.addElement(s);
        balist.setModel(bre);
    }//GEN-LAST:event_addbActionPerformed

    private void rmvbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rmvbActionPerformed
        int index = balist.getSelectedIndex();
        System.out.println("Index Selected: " + index);
        DefaultListModel rm = (DefaultListModel) balist.getModel();
        if (index >= 0) {
            rm.removeElementAt(index);
            balist.setModel(rm);
            JOptionPane.showMessageDialog(this, "Selection was removed!");
        }
        else {
            JOptionPane.showMessageDialog(this, "No Selection was selected!");
        }
    }//GEN-LAST:event_rmvbActionPerformed

    private void orderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_orderActionPerformed
        DefaultListModel returning = (DefaultListModel) balist.getModel();
        int i = returning.getSize(); 
        while (i > 0) {
            String index = returning.getElementAt(i-1).toString();
            String[] s = index.split(" - ");
            String id = s[0].trim();
            String name = s[1].trim();
            returning.removeElementAt(i-1);
            String stm = "Update book set exist_in_database = 1 where b_id = \"" + id + "\" and b_name = \"" + name + "\"";
            System.out.println(stm);
            bupdate(stm);
            i--;
        }
        JOptionPane.showMessageDialog(this, "Book(s) added to library");
        balist.setModel(returning);
        refresh();
    }//GEN-LAST:event_orderActionPerformed
    void bupdate(String stm) {
        try {
            Connection connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/number_1", "root","");
            Statement sta = connection.createStatement();
            sta.executeUpdate(stm); 
            connection.close();
        } catch (Exception exception) {
            exception.printStackTrace();
        }
    }
    
